import React from "react";
import styled from "styled-components";

const HeaderContainer = styled.header`
  background-color: ${({ theme }) => theme.colors.headerFooterBg};
  color: ${({ theme }) => theme.colors.headerFooterText};
  padding: 10px;
  text-align: center;
  position: sticky;
  top: 0;
  z-index: 1000;
`;

const SearchInput = styled.input`
  border: 1px solid #ccc;
  border-radius: 4px;
  padding: 6px;
  margin-top: 10px;
`;

const ThemeToggleButton = styled.button`
  margin-top: 10px;
  padding: 6px 12px;
  border: none;
  border-radius: 4px;
  cursor: pointer;
  background-color: ${({ theme }) => theme.colors.buttonBg};
  color: #fff;

  &:hover {
    background-color: ${({ theme }) => theme.colors.buttonHoverBg};
  }
`;

function Header({ searchText, onSearch, toggleTheme, isDarkMode }) {
  return (
    <HeaderContainer>
      <h1>Ferramentas para Desenvolvedores Web</h1>
      <SearchInput
        type="text"
        placeholder="Buscar ferramenta..."
        value={searchText}
        onChange={(e) => onSearch(e.target.value)}
      />
      <br />
      <ThemeToggleButton onClick={toggleTheme}>
        {isDarkMode ? "‚òÄÔ∏è Modo Claro" : "üåô Modo Escuro"}
      </ThemeToggleButton>
    </HeaderContainer>
  );
}

export default Header;



