    <AppContainer>
      <Header searchText={searchText} onSearch={setSearchText} />
      <MainLayout>
        <Aside
          selectedCategory={selectedCategory}
          onCategorySelect={setSelectedCategory}
        />
        <Home selectedCategory={selectedCategory} searchText={searchText} />
      </MainLayout>
      <Footer />
